import React, { useState, useEffect } from 'react';
import axios from 'axios';
import TableCard from './TableCard'; // We will create this component next

const TableGrid = () => {
  const [tables, setTables] = useState([]);

  useEffect(() => {
    // Fetch table data from the backend
    axios.get('http://localhost:5000/api/tables')
      .then(response => setTables(response.data))
      .catch(error => console.error(error));
  }, []);

  return (
    <div className="grid grid-cols-30 gap-1 p-4">
      {tables.map((table) => (
        <TableCard key={table.number} table={table} />
      ))}
    </div>
  );
};

export default TableGrid;
