// src/components/TableGrid.js
import * as React from 'react';
import Grid from '@mui/material/Grid';
import Paper from '@mui/material/Paper';
import { Modal, TextField, Button, Box } from '@mui/material';

export default function TableGrid() {
  // Number of columns and rows
  const columns = 12; // 12 columns
  const rows = 12; // 12 rows
  const totalCells = columns * rows;
  
  // Define cell size
  const cellSize = 50;

  // State to track which cell is clicked and the table details
  const [open, setOpen] = React.useState(false);
  const [selectedCell, setSelectedCell] = React.useState(null);
  const [tableData, setTableData] = React.useState({
    name: '',
    time: '',
    phone: '',
  });

  // Handle opening the modal and selecting a cell
  const handleCellClick = (index) => {
    setSelectedCell(index);  // Store the index of the clicked cell
    setOpen(true);            // Open the modal
  };

  // Handle form changes
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setTableData({
      ...tableData,
      [name]: value,
    });
  };

  // Handle modal close and save data
  const handleClose = () => {
    setOpen(false);
    // Optionally, save or process the table data here
    console.log(`Table ${selectedCell + 1} booked with details:`, tableData);
    // Reset table data after saving
    setTableData({
      name: '',
      time: '',
      phone: '',
    });
  };

  return (
    <div className="table-grid-container">
      <Grid container spacing={0.2} justifyContent="center" sx={{ flexGrow: 1 }}>
        {new Array(totalCells).fill(null).map((_, index) => (
          <Grid item xs={1} key={index} style={{ display: 'flex', justifyContent: 'center' }}>
            <Paper
              sx={{
                height: `${cellSize}px`, // Fixed cell size
                width: `${cellSize}px`,  // Fixed cell size
                backgroundColor: selectedCell === index ? '#1976d2' : '#e0e0e0', // Color change on click
                display: 'flex',
                justifyContent: 'center',
                alignItems: 'center',
                cursor: 'pointer',
                '&:hover': { backgroundColor: '#1565c0' }, // Hover effect
                margin: '0.5px', // Smaller padding between cells
              }}
              onClick={() => handleCellClick(index)} // Open modal on cell click
            />
          </Grid>
        ))}
      </Grid>

      {/* Modal for booking details */}
      <Modal
        open={open}
        onClose={handleClose}
        aria-labelledby="table-booking-modal"
        aria-describedby="modal-to-enter-table-booking-details"
      >
        <Box
          sx={{
            position: 'absolute',
            top: '50%',
            left: '50%',
            transform: 'translate(-50%, -50%)',
            backgroundColor: 'white',
            padding: 4,
            boxShadow: 24,
            width: 300,
          }}
        >
          <h2>Table {selectedCell + 1} Booking</h2>
          <TextField
            label="Customer Name"
            fullWidth
            margin="normal"
            name="name"
            value={tableData.name}
            onChange={handleInputChange}
          />
          <TextField
            label="Booking Time"
            fullWidth
            margin="normal"
            name="time"
            value={tableData.time}
            onChange={handleInputChange}
          />
          <TextField
            label="Phone Number"
            fullWidth
            margin="normal"
            name="phone"
            value={tableData.phone}
            onChange={handleInputChange}
          />
          <div style={{ marginTop: '16px', display: 'flex', justifyContent: 'space-between' }}>
            <Button variant="contained" color="primary" onClick={handleClose}>
              Save
            </Button>
            <Button variant="outlined" color="secondary" onClick={handleClose}>
              Cancel
            </Button>
          </div>
        </Box>
      </Modal>
    </div>
  );
}
