// src/components/TableGrid.js
import * as React from 'react';
import Grid from '@mui/material/Grid';
import Paper from '@mui/material/Paper';

export default function TableGrid() {
  // Number of columns and rows
  const columns = 15;
  const rows = 10;
  const totalCells = columns * rows;
  
  // Define cell size (we'll use the container width and set the cell size accordingly)
  const cellSize = 50; // Adjust this value for bigger cells
  
  // Handle cell click event
  const handleClick = (index) => {
    console.log(`Square ${index + 1} clicked`); // Log the clicked square number
  };

  return (
    <div className="table-grid-container">
      <Grid container spacing={0.5} justifyContent="center" sx={{ flexGrow: 1 }}>
        {new Array(totalCells).fill(null).map((_, index) => (
          <Grid item xs={1} key={index} style={{ display: 'flex', justifyContent: 'center' }}>
            <Paper
              sx={{
                height: `${cellSize}px`, // Fixed cell size
                width: `${cellSize}px`,  // Fixed cell size
                backgroundColor: '#1976d2',
                display: 'flex',
                justifyContent: 'center',
                alignItems: 'center',
                cursor: 'pointer',
                color: 'white',
                '&:hover': { backgroundColor: '#1565c0' }, // Hover effect
              }}
              onClick={() => handleClick(index)}
            >
              <p className="table-number" style={{ fontSize: '12px' }}>{`Table ${index + 1}`}</p>
            </Paper>
          </Grid>
        ))}
      </Grid>
    </div>
  );
}
