import React, { useState } from 'react';
import BookingForm from './BookingForm'; // We will create this component next

const TableCard = ({ table }) => {
  const [showForm, setShowForm] = useState(false);

  const handleClick = () => {
    setShowForm(!showForm);
  };

  return (
    <div className={`p-4 border ${table.combinable ? 'bg-green-500' : 'bg-gray-500'}`} onClick={handleClick}>
      <h4>{`Table ${table.number}`}</h4>
      {showForm && <BookingForm table={table} />}
    </div>
  );
};

export default TableCard;
