import React, { useState } from 'react';
import axios from 'axios';

const BookingForm = ({ table }) => {
  const [name, setName] = useState('');
  const [phone, setPhone] = useState('');
  const [time, setTime] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    
    const bookingData = {
      name,
      phone,
      time,
    };

    axios.post(`http://localhost:5000/api/tables/${table.number}/book`, bookingData)
      .then(response => {
        console.log('Booking successful:', response);
      })
      .catch(error => console.error('Error booking table:', error));
  };

  return (
    <form onSubmit={handleSubmit} className="space-y-4">
      <div>
        <label htmlFor="name" className="block">Customer Name:</label>
        <input
          type="text"
          id="name"
          value={name}
          onChange={(e) => setName(e.target.value)}
          className="p-2 border rounded"
          required
        />
      </div>
      <div>
        <label htmlFor="phone" className="block">Phone Number:</label>
        <input
          type="text"
          id="phone"
          value={phone}
          onChange={(e) => setPhone(e.target.value)}
          className="p-2 border rounded"
          required
        />
      </div>
      <div>
        <label htmlFor="time" className="block">Booking Time:</label>
        <input
          type="datetime-local"
          id="time"
          value={time}
          onChange={(e) => setTime(e.target.value)}
          className="p-2 border rounded"
          required
        />
      </div>
      <button type="submit" className="bg-blue-500 text-white p-2 rounded">Book Table</button>
    </form>
  );
};

export default BookingForm;
