// src/components/TableGrid.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import TableCard from './TableCard';

const TableGrid = () => {
  const [tables, setTables] = useState([]);

  useEffect(() => {
    // Mock backend API call to fetch tables data
    axios.get('http://localhost:5000/api/tables')
      .then(response => setTables(response.data))
      .catch(error => console.error(error));
  }, []);

  // Calculate the number of rows dynamically based on tables data
  const rows = Math.ceil(tables.length / 30);

  return (
    <div className="grid grid-cols-30 gap-1 p-4">
      {Array.from({ length: rows }).map((_, rowIndex) => (
        [...Array(30)].map((_, colIndex) => {
          const tableIndex = rowIndex * 30 + colIndex;
          if (tables[tableIndex]) {
            return (
              <TableCard key={tables[tableIndex].number} table={tables[tableIndex]} />
            );
          }
          return <div key={colIndex} className="w-12 h-12 border" />; // Empty cell
        })
      ))}
    </div>
  );
};

export default TableGrid;
